using System;
using System.Net.Mime;
using System.Threading;
using Microsoft.AspNetCore.Mvc;

namespace Bard.Sample.Api.Controllers
{
    [ApiController]
    [Route("api/misc")]
    public class MiscController : ControllerBase
    {
        private const string Image =
            "";

        [HttpGet("image")]
        public IActionResult Index()
        {
            var imageByteArray = Convert.FromBase64String(Image);
            return File(imageByteArray, MediaTypeNames.Image.Jpeg);
        }

        [HttpGet("slow")]
        public ActionResult<bool> SlowEndPoint()
        {
            Thread.Sleep(3000);

            return Ok(true);
        }
    }
}